import 'package:freezed_annotation/freezed_annotation.dart';
import 'package:montra/constants/income_source.dart';

// required: associates our `main.dart` with the code generated by Freezed
part 'income_model.freezed.dart';
// optional: Since our IncomeModel class is serializable, we must add this line.
// But if IncomeModel was not serializable, we could skip it.
part 'income_model.g.dart';

@freezed
abstract class IncomeModel with _$IncomeModel {
  const factory IncomeModel({
    required String incomeId,
    required int amount,
    required String userId,
    required IncomeSource source,
    String? attachment,
    String? description,
    @JsonKey(fromJson: _dateTimeFromJson, toJson: _dateTimeToJson)
    required DateTime createdAt,
    String? bankName,
    String? walletName,
  }) = _IncomeModel;

  factory IncomeModel.fromJson(Map<String, dynamic> json) =>
      _$IncomeModelFromJson(json);
}

// Custom converters for DateTime
DateTime _dateTimeFromJson(String date) => DateTime.parse(date);
String _dateTimeToJson(DateTime date) => date.toIso8601String();
